package vn.edu.fit.iuh.daos;import org.springframework.jdbc.core.JdbcTemplate;import javax.sql.DataSource;import java.util.List;public abstract class AbstractDao<T,ID> {    private DataSource dataSource;    protected JdbcTemplate jdbcTemplate;    public AbstractDao(DataSource dataSource) {        this.dataSource = dataSource;     jdbcTemplate=new JdbcTemplate(dataSource);    }    public abstract void insert(T entity);    public abstract T getById(ID id);    public  abstract List<T> getAll();    public abstract  boolean delete(ID id);}