package vn.edu.fit.iuh.daos;import org.springframework.jdbc.core.BeanPropertyRowMapper;import org.springframework.stereotype.Component;import vn.edu.fit.iuh.enities.Adress;import javax.sql.DataSource;import java.util.List;import java.util.UUID;@Componentpublic class AddressDAO extends AbstractDao<Adress, UUID> {    public AddressDAO(DataSource dataSource) {        super(dataSource);    }    @Override    public void insert(Adress entity) {        String sql="insert into address values(?,?,?,?,?,?)";        jdbcTemplate.update(sql,entity.getId(),entity.getStreet(),entity.getCity(),entity.getCountry(),entity.getNumber(),entity.getZipcode());    }    @Override    public Adress getById(UUID uuid) {        String sql="select *from address where id=?";        return  jdbcTemplate.queryForObject(sql,new BeanPropertyRowMapper<>(Adress.class),uuid);    }    @Override    public List<Adress> getAll() {        String sql="select *from address";        return  jdbcTemplate.query(sql,new BeanPropertyRowMapper<>(Adress.class));    }    @Override    public boolean delete(UUID uuid) {        String sql="delete from address where id=?";        int rs=jdbcTemplate.update(sql,uuid);        return rs>0;    }}