package vn.edu.fit.iuh.daos;import org.springframework.jdbc.core.BeanPropertyRowMapper;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Component;import vn.edu.fit.iuh.enities.Skill;import javax.sql.DataSource;import java.util.List;import java.util.UUID;@Componentpublic class SkillDAO extends  AbstractDao<Skill, UUID>{    public SkillDAO(DataSource dataSource) {        super(dataSource);    }    @Override    public void insert(Skill skill) {String sql="insert into skill values(?,?,?,?)";jdbcTemplate.update(sql,skill.getSkill_id(),skill.getSkill_description(),skill.getSkill_name(),skill.getType().getValue());    }    @Override    public Skill getById(UUID uuid) {        String sql="select *from skill where skill_id=?";        return jdbcTemplate.queryForObject(sql,new BeanPropertyRowMapper<>(Skill.class),uuid);    }    @Override    public List<Skill> getAll() {        String sql="select *from skill";        return jdbcTemplate.query(sql,new BeanPropertyRowMapper<>(Skill.class));    }    @Override    public boolean delete(UUID uuid) {        String sql="delete from skill where skill_id=?";           int rs= jdbcTemplate.update(sql,uuid);           return rs>0;    }}